mapper.select:
select b.*,u.username as userName,u.phoneNumber from books b
left join users u on u.userid = b.userId
where b.isDeleted = 0

    and b.bookName like #{params.bookName} 


    and b.bookType like #{params.bookType}


    and b.borrowType like #{params.borrowType} 


    and b.userId = #{params.userId}

select count(*) from books


        and bookName like #{bookName}


    and b.bookType like #{params.bookType}


    and b.borrowType like #{params.borrowType} 


    and userId = #{params.userId}

mapper.update:
update books


        bookName=#{bookName},


        bookType=#{bookType},


        borrowType=#{borrowType}


where bookId=#{bookId}
