mapper.select:
select id, syllabus_id as syllabusId, source_name as sourceName, week_type as weekType, serial as serial, is_single as isSingle, teacher_id as teacherId, class_id as classId from syllabus_sources where is_delete = 0
and syllabus_id = #{params.syllabusId}
order by week_type,serial LIMIT #{pageStart},#{pageSize}

select s.id as detailId, s.syllabus_id as syllabusId, s.source_name as sourceName, s.week_type as weekType, s.serial as serial, s.is_single as isSingle, s.teacher_id as teacherId, u.user_name as teacherName, s.class_id as classId, c.class_name as className from syllabus_sources s left join users u on u.id=s.teacher_id left join class c on c.id=s.class_id where s.is_delete = 0
and s.syllabus_id = #{syllabusId}
order by week_type,serial
